  const items = (event) => {
    finalInput(event.target.value)
  }
  const submitBtn = () => {
    newSubmit((getValue) => {
      return [...getValue, inputList]
    });
    finalInput("")
  };

  const deleteItems = (id) => {
    console.log("deleted");

    newSubmit((getValue) => {
      return getValue.filter((arrElem, index) => {
        return index !== id;
      });
    });

  };




